import hotBg from './assets/sunny.jpeg';
import coldBg from './assets/cold.jpg';
import Description from './components/Descriptions.jsx';
import { useEffect, useState } from 'react';
import { getWeatherData } from "./weather.js";

function App() {

  const [weather, setWeather] = useState(null);
  const [units, setUnits] = useState('metric');
  useEffect(() => {
    const fetchWeatherData = async () => {
      const data = await getWeatherData('nairobi', units);
      setWeather(data)

    };
    fetchWeatherData();


  }, []);

  const handleUnitsClick = (e) => {
  const button = e.CurrentTarget;
  const currentUnit = button.innerText.slice()
  console.log(button)
  }

  return (
    <div className='app' style={{ background: `url(${coldBg})` }}>
      <div className='overlay'>
        {
          weather && (
            <div className='container'>
              <div className='section section__inputs'>
                <input type='text' name='city' placeholder='Enter City..'
                />
                <button onClick={(e) => handleUnitsClick(e)}>F</button>

              </div>
              <div className='section section__temperature'>
                <div className='icon'>
                  <h3>{`${weather.name}, ${weather.country}`}</h3>
                  <img src={weather.iconURL} alt="weatherIcon" />
                  <h3>{weather.description}</h3>
                </div>
                <div className='temperature'>
                  <h1>{`${weather.temp.toFixed()} ${units === 'metric' ? 'C' : 'F'}`}</h1>

                </div>

              </div>
              <Description weather={weather} units={units}/>
            </div>

          )
        }

      </div>
    </div>

  );
}

export default App;
